bool process_record_user(uint16_t keycode, keyrecord_t *record) {
  if (record->event.pressed) {
#ifdef OLED_ENABLE
    set_keylog(keycode, record);
#endif
    // set_timelog();
  }
  return true;
}

bool encoder_update_user(uint8_t index, bool clockwise) {


  if (index == 0) {
    switch(biton32(layer_state)){
      case 1:
        if (clockwise) {
          tap_code(KC_MNXT);
        } else {
          tap_code(KC_MPRV);
        }
        break;

      case 2:
        if (clockwise) {
          register_code(KC_LCTL);
          tap_code(KC_PGDN);
          unregister_code(KC_LCTL);
        } else {
          register_code(KC_LCTL);
          tap_code(KC_PGUP);
          unregister_code(KC_LCTL);
        }
        break;

      default:
        if (clockwise){
            tap_code(KC_VOLU);
        } else {
            tap_code(KC_VOLD);
        }
        break;
    }
  }

  return true;
}